package com.qa.main;

public class Runner {

	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 99; i >= 1;) {
			  System.out.println(i + " bottles of beer on the wall, 99 bottles of beer. ");
			  i-=1;
			  System.out.println("Take one down and pass it around, " + i +" bottles of beer on the wall.");
			  
			}
		System.out.println("No more bottles of beer on the wall, no more bottles of beer.\r\n" + 
				"Go to the store and buy some more, 99 bottles of beer on the wall.");
	


		
	}

}
